void mainImage(out vec4 o,vec2 u){
    vec2 uv = (u-iResolution.xy/2.)/iResolution.x*8./exp2(iTime/6.);
    uv-=vec2(.524917605448);
    vec2 z = vec2(0);
    float it;
    for(it=0.;it<1000.;it++){
        z = mat2(z,-z.y,z.x)*z+uv;
        if(dot(z,z)>65536.)break;
    }
    it -= log2(log2(dot(z,z)));
    o = vec4(sin(it/16.+vec3(0,.3,.4)*6.28-iTime/4.-2.)/2.+.5,0);
    if(isnan(it))o.xyz = vec3(.05,.1,.15);
}
