{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wiggle =sin(iTime*2.0+(uv.x*5.0))*0.01;//(velocidade)*amplitude
   float wi=wiggle*0.01;

    fragColor = vec4(0,0,0,0);

    if(uv.y>0.33+wiggle){
          fragColor = vec4(1,1,1.0,1);
    }

    if(uv.y>0.66+wiggle){
          fragColor = vec4(0.,0.0,0.7,1);
