vec3 col; 

void circle(float x, float y, float s, vec2 uv) {
    uv.x += x;
    uv.y += y;
    float d = length(uv/s);
    float c = smoothstep(.1, .09, d);

    col += c;
}

void square(float x, float y, float s, vec2 uv) {
    uv *= -1.;

    float left = step(x, uv.x);
    float bottom = step(y, uv.y);

    float p = left * bottom;

    col += vec3(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*-1.)/iResolution.y;

    circle(0.2, 0.3, 0.8, uv);
    circle(0.6, 0.4, 2., uv);

    square(0.4, 0.2, 0.3, uv);


    fragColor = vec4(col, 1.0);
}
