#define pi 3.14159

vec3 func(float t, float o) {
    return vec3(cos(t), cos(t + o), cos(t - o));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;

    float t = 0.25 * iTime;
    uv -= 0.05 * vec2(sin(t), cos(t));

    float a = atan(uv.x, uv.y);
    float r = 0.5 + 0.5 * sin(iTime);
    r -= cos(a + t + cos(sin(a + t)) + cos(cos(a - t)));  

    float d = length(uv);
    d *= 0.25 * (1. + cos(2. * iTime) - sin(2. * iTime));

    float o = 2. * pi / 3.; 
    vec3 p1 = d * (1. + func(iTime, o));
    vec3 p2 = 0.1 + 0.05 * func(0.125 * iTime, o);

    vec3 col = 1. - smoothstep(vec3(r), p1, p2);

    fragColor = vec4(col,1.0);      
}


