// Fork of "modus6 [cube julia set]" by modus. https://shadertoy.com/view/sdsGDl 2021-03-29
// Modus6JuliaFractal.glsl by THolzer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 m = 0.5*iResolution.xy; 
  vec2 z = 2.5*(fragCoord - m) / iResolution.y;
  if (iMouse.x > 0.0) m.xy = 2.5*(iMouse.xy - m) / iResolution.y;
  if (iMouse.z <= 0.0) m = 0.8*vec2(sin(0.1*iTime), cos(0.1*iTime));
  vec3 col = vec3(1.0);
  for(float i=0.; i<64.; i++)
  {
    z = vec2(     z.x*z.x*z.x -3.0*z.x*z.y*z.y
            , 3.0*z.x*z.x*z.y -    z.y*z.y*z.y) + m;
    float lz = length(z);
    if(lz > 4.0)
    {
      col = vec3(-lz / i + 1.0); 
      break;
    }
  }
  fragColor = vec4(col,1.0);
}
