#define PI 3.1416

vec3 hsv2rgb(in vec3 c)
{
  c.x/=360.0;
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx,0.0,1.0), c.y);
}

vec4 drawColorRing(in vec2 uv){
    float UV = floor(1.0-uv.y );
    float rad = UV * 2.0 * PI+atan(uv.y,uv.x);
    float degree = rad/(2.0*PI)*360.0;
    return vec4(hsv2rgb(vec3(degree,1.0,1.0)),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	fragCoord-=iResolution.xy*vec2(0.5);
    vec2 UV=fragCoord/(iResolution.y*0.5);

    vec4 ringColor = drawColorRing(UV);
    fragColor=vec4(0.15,0.15,0.15,1.0);
    fragColor=fragColor*(1.0-ringColor.a)+ringColor;
}
