vec4 col_red = vec4(1.0, 0.2, 0.1, 1.0);
vec4 col_grey = vec4 (0.4, 0.4, 0.45, 1.0);

void circle(inout vec4 fragColor, vec2 uv, vec2 c, float r, vec3 col)
{
    float l = length(uv.xy - c.xy);
    if (l < r) fragColor += vec4(col.rgb, 1.0);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float r = (1.0 + sin(iTime)) / 4.0; // (время в секундах) -> (0.0 ... 0.5)
    vec2 c = vec2(0.5, 0.5);
    fragColor = col_grey;
    circle(fragColor, uv, c, r, col_red.rgb);  
}
