void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;
    float x= uv.x;
    float y= uv.y;
    vec3 col;
    if(abs(length(uv-vec2(0.5))-0.5)<1./iResolution.y)
        col=vec3(1,0,0);

    else
        col=vec3(0,0,1);


    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime*vec3(20)+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
