#define SS(b,f) smoothstep(-b,b,f)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float col;
    // Time varying pixel color
    for(int i=0; i<6;i++){
        col += SS(0.005,-abs(cos(iTime + uv.x*9.0*(0.6+float(i)*0.02*sin(iTime)))*0.1 -uv.y +0.7));
    }

    // Output to screen
    fragColor = vec4(col);
}
