// Colorization and output stuff here.
void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float val = texelFetch(iChannel0, ivec2(fragCoord / 4.0), 0).x;

    fragColor.r = fract(val * 137.0);
    fragColor.g = fract(val * 713.0);
    fragColor.b = fract(val * 435.0);
}

#define GET(d) texelFetch(iChannel0, ivec2(mod(fragCoord + vec2(d) + res, res)), 0).x

// Change these constants to adjust behavior of CA
const float A = 0.11;
const float B = 0.80;
const float C = 2.5;
const float D = 1.0;

// Alternatively, you might change how 'hash' is computed instead

const float MAX_VAL = 1.0;
const float NUM_NEIGH = 9.0;

// Simple time-dependant noise function, for seeding CA
float noise(in vec2 co){
    float a = fract(co.x * 10.5 + co.y * 7.5 + fract(iDate.a));
    a = fract(715.5 * a * a + 57.1 * co.x);
    a = fract(1371.5 * a * a + 757.1 * co.y);
    return a;
}

// Main CA computation
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 res = iResolution.xy;

    if (iFrame > 0) {
    	ivec2 d;

        float cen = GET(vec2(0.0, 0.0));
        float sum = 0.0;

    	for(d.y = -1; d.y <= +1; d.y++)
        	for(d.x = -1; d.x <= +1; d.x++)
            	sum += GET(d);

        // Value used to choose next state of the cell
        float hash = A * MAX_VAL * (NUM_NEIGH + D) + B * ((C / NUM_NEIGH) * sum+cen) - sum;

        float min_dist = 100000.0, val;

        // Find neighbor with value closest to 'hash'
        for(d.y = -1; d.y <= +1; d.y++)
            for(d.x = -1; d.x <= +1; d.x++){
                float new_val = GET(d);
                if (abs(new_val - hash) < min_dist){
                    min_dist = abs(new_val - hash);
                    val = new_val;
                }
            }

        // Copy its state
        fragColor.x = val;
    } else {
        // Randomize initial state on zeroth frame
        fragColor.x = noise(fragCoord/res);
    }
}

// Flicker reduction.
// This CA tends to produce structures that flicker between two states.
// Code here copies CA output on odd frames and stays unchanged on even.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (iFrame % 2 == 1)
        fragColor.x = texelFetch(iChannel0, ivec2(fragCoord), 0).x;
    else
        fragColor.x = texelFetch(iChannel1, ivec2(fragCoord), 0).x;
}
