#define GridSize 30.
#define Thickness 0.3


float pi = 3.14159;
mat2 rot(float a){
	return mat2(cos(a),-sin(a),sin(a),cos(a));
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord;
    uv*=rot(45.*pi/180.);
    uv.x += iTime * 20.;
    uv /= GridSize;

    vec2 id = floor(uv);
	uv = fract(uv)-0.5;

    float rnd = rand(id);
    if(rnd <0.5){
    	uv.x *= -1.;
    }

    float v = min(abs(uv.x+uv.y-0.5), abs(uv.x+uv.y+0.5));
    float wall = step((1. - Thickness) * 0.5, v);

    fragColor = vec4(wall * vec3(1,.84,0.),1.0);
    //fragColor = vec4(vec3(v), 1.);
}
