void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int blurSize = 5; // take that many pixels in each direction, so (blurSize+1)^2 pixels total

    if(fragCoord.x < iResolution.x/2.0) {
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    }
    else {
        fragColor = vec4(0,0,0,0);
        for(int x = -blurSize; x <= blurSize; x++) {
            for(int y = -blurSize; y <= blurSize; y++) {
                vec2 coord = fragCoord + vec2(float(x), float(y));
                fragColor += texture(iChannel0, coord/iResolution.xy) / pow(float(2*blurSize+1), 2.0);
            }
        }
    }
}
