    bool circle (vec2 point, vec2 centr, float r){
               float dx = (point.x - centr.x) * (point.x - centr.x);
               float dy = (point.y - centr.y) * (point.y - centr.y);
                return dx + dy < r*r; 
    }

    bool lineOver(vec2 uv, vec3 line) 
    {
                return uv.y * line.y >= uv.x * line.x + line.z;
    }


    bool lineUnder(vec2 uv, vec3 line) 
    {
                return uv.y * line.y <= uv.x * line.x + line.z;
    }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Remap the space to -1. to 1.
    vec2 uv = 2.0*fragCoord/iResolution.xy-1.0;
     uv.x *= iResolution.x/iResolution.y;


    //colors
    vec4 red = vec4(1.0, 0.2, 0.2, 1.0);
    vec4 darkred = vec4(0.7, 0.1, 0.1, 1.0);
    vec4 white = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 body = vec4(1.0, 0.8, 0.4, 1.0);
    vec4 pink = vec4(1.0, 0.4, 0.4, 0.5);


    // Output to screen
    fragColor = vec4(1.0,1.0,1.0,1.0);

     if(circle(uv, vec2(-0.7, 0.0), 0.35))
         fragColor = body;

     if(lineOver(uv, vec3 (1.0, -16.0,  0.7)) && lineUnder(uv,vec3(-1.,-16.,1.7)) && uv.x > -0.5)
         fragColor = body;

     if(circle(uv, vec2(-0.56, 0.04), 0.08))
         fragColor = black;

     if(circle(uv, vec2(-0.568, 0.055), 0.08))
         fragColor = body;

     if(circle(uv, vec2(-0.56, -0.23), 0.08))
         fragColor = black;

     if(circle(uv, vec2(-0.54, -0.21), 0.08))
         fragColor = body;

     if(circle(uv, vec2(-0.66, -0.22), 0.08))
         fragColor = body;

     if(circle(uv, vec2(-0.66, -0.17), 0.08))
         fragColor = pink;

     if (uv.x > -1.05 && uv.x<-0.35 && uv.y > 0.15 && uv.y < 0.3) 
      fragColor = darkred;

     if(circle(uv, vec2(-0.37, 0.23), 0.083))
         fragColor = darkred;

     if(circle(uv, vec2(-1.03, 0.23), 0.083))
         fragColor = darkred;

     if(lineOver(uv, vec3(-2.1, -1.0, -2.5)) && lineUnder(uv, vec3(-2.4, 1.0, -0.5)) && uv.y >= 0.24 && uv.y <= 0.9) 
         fragColor = darkred;

     if(circle(uv, vec2(-0.68, 0.85), 0.15))
         fragColor = darkred;

     if(uv.x < -0.6 && uv.x > -0.8 && uv.y < -0.3 && uv.y > -0.5)
         fragColor = body;

     if(uv.y+0.45 < 0.01-pow(abs(uv.x+0.7)*2.,2.0))
         fragColor = red;

     if (uv.x < -0.61 && uv.x > -0.79 && uv.y < -0.7 && uv.y > -0.9)
         fragColor = darkred;

     if(circle(uv, vec2(-0.7, -0.75), 0.1))
        fragColor = darkred;

     if(circle(uv, vec2(-0.7, -0.9), 0.1))
        fragColor = darkred;

     for (float i = 0.05; i < 0.1; i+=0.15){
         if(circle(uv, vec2(-0.7+i, -0.9-i), 0.1))
             fragColor = darkred;
     }

}
