vec2 hash( vec2 p ){
	p = vec2( dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3)));
	return fract(sin(p)*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 uv1 = (fragCoord)/iResolution.xy;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float d = .0;
    float minDist = 100.;
    uv *= 3.;
    vec2 gv = fract(uv)-1.;
    vec2 id = floor(uv);
    vec4 cellid = vec4(.0);

    for(float x = -1.; x < 1.; x++){
        for(float y = -1.; y < 1.; y++){
            vec2 offset = vec2(y, x);

            vec2 p = hash(id + offset);

            vec2 m = offset + cos(p*iTime)*3.;

            float l = length(gv - m);

             if(l < minDist){
                    minDist = l;
                    cellid = texture(iChannel0, vec2(uv1.x*.9+minDist*.02, uv1.y*(.5*(1. + sin(iTime*.4)))+minDist*sin(iTime)*.01));
                    cellid.r *= .4/cellid.b-.3;
                    cellid.g *= .3/cellid.r-.5;
                    cellid.b *= .3/(cellid.r+cellid.g);
                }
        }
    }



    // Output to screen
    fragColor = vec4(cellid);
}
