float hash(vec2 p) {
    p = fract(p * vec2(369.12, 420.12));
    p += dot(p, p + 34.23);
    return fract(p.x * p.y);
}

void mainImage( out vec4 res, in vec2 coord )
{
    vec3 scr = iResolution;
    vec3 col = vec3(.5, 0, .5);
    float zoom = 15., drift = iTime * .66, thck = .25;

    vec2 uv = (coord - .5 * scr.xy) / scr.y * zoom;
    uv += drift;
    float id = hash(floor(uv));

    vec2 gv = fract(uv) - .5;
    if (id < .3) gv.x *= -1.; 
    if (id > .7) gv.y *= -1.;
    float delta = abs(abs(gv.x + gv.y) - .5);
    float diag = smoothstep(.01, -.01, delta - thck);

    col.rg += diag;
    col.g -= diag / 2.;

    res = vec4(col,1.0);
}
