mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}


void mainImage( out vec4 C, in vec2 FC )
{
    float o,c,i,e,f,s,g,k=.01;
    vec2 r = iResolution.xy;
    float t = iTime;
    for(o++,c++;i++<1e2;g+=min(f,max(.03,e))*.7)
        {s=2.;vec3 p=vec3((FC.xy-r/s)/r.y*g,g-s);
        p.yz*=rotate2D(-.8);
        p.z+=t;
        for(e=f=p.y;s<2e2;s/=.6)
            p.xz*=rotate2D(s),
            e+=abs(dot(sin(p*s)/s,p-p+.3)),
            f+=abs(dot(sin(p.xz*s*.6)/s,r/r));
        o+=(f>k*k?e:-exp(-f*f))*o*k;}
    c+=e;

    C = vec4(o);
    C.rg -= c*.04;

}
