float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float smile(vec2 uv, vec2 p, float size) {
uv -= p;
uv /= size;
vec2 a = vec2(.2, -.1);
float c = Circle(uv, a, .4, .05);
c = c-Circle(uv, vec2(0, 0), .05, .01);
c = c-Circle(uv, vec2(0.4, 0), .05, .01);

//mouth
float mouth = Circle(uv, vec2(0.2,-.1), .28, .02);

mouth -= Circle(uv, vec2(0.2,-0.02), .28, .02);
c=c-mouth;
return c;


}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord/iResolution.xy;
uv -=0.5;
uv.x *= iResolution.x/iResolution.y;

float t = iTime;
float t1 = iTime+5.0;
vec2 p = vec2((t)*cos(t)*0.02, (t)*sin(t)*0.01);
float mask = smile(uv, p, .1);

vec2 p1 = vec2((t1)*cos(t1)*0.02, (t1)*sin(t1)*0.01);
float mask1=smile(uv, p1, .1);
//mask = mask + mask1;
//float mask1 = smile(uv, vec2(0.02), .5);


mask = mask + mask1;
vec3 col = vec3(1., 0., 0.) * mask;
fragColor = vec4(col, 1.0);


}
