renderpass.code:
float triangle(float x)
{
    return abs(mod(x, 1.0) * 2.0 - 1.0);
}

float noise(vec3 pos)
{
    vec3 n = texture(iChannel0, pos.xy).rgb;
    float t = 2. * triangle(pos.z);
    if (t < 1.) //bad code, be more cleverer than me
        return mix(n.r, n.g, t); 
    else
        return mix(n.g, n.b, t - 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(vec3(1.0) * noise(vec3(uv, iTime * 0.1)),1.0);
}
