#define R 0.5
#define PI 3.141592653589793238462643

vec3 vstep( in float v, in float os, in float lev )
{
     vec3 vs = v+os > 0.0 ? vec3(0.0) : vec3(lev);
     return vs;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv -= fract(texture(iChannel0, uv-vec2(iTime/2.0,0.0))/2.0).r*0.01;

    float d = length(uv)-R;

    float val = sin(16.0*(4.0*d-(PI/24.0)*iTime)+d*2.0)*cos(4.0*(16.0*d+(PI/24.0)*iTime)-d*2.0);

    vec3 vs = vstep(val,0.0,0.8);
    vec3 v2s = vstep(val,0.5*d,0.8);
    vec3 v3s = vstep(val,d,0.8);

    vec3 col = vec3(vs.r,v2s.g,v3s.b);

    fragColor = vec4(col,1.0);
}
