float squareWave750( float f, float x)
{
    return (min(1.0, floor( 4.0 * floor( f * x ) - floor( 4.0 * f * x ) + 3.0 )));
}

float squareWave500( float f, float x)
{
    return (floor( 2.0 * floor( f * x ) - floor( 2.0 * f * x ) + 1.0 ));
}

float squareWave250( float f, float x)
{
    return (max(0.0, floor( 4.0 * floor( f * x ) - floor( 4.0 * f * x ) + 1.0 )));
}

vec2 squareWave250( vec2 f, float x)
{
    return (max(vec2(0.0), floor( 4.0 * floor( f * x ) - floor( 4.0 * f * x ) + 1.0 )));
}

float triangleWave500( float q, float x)
{
    float f = 1.0-2.0*abs(0.5-mod(q*x,1.0));
    return (f);
}

float triangleWave250( float q, float x)
{
    float f = max(0.0,1.0-2.0*abs(0.5-2.0*mod(q*x,1.0)));
    return (f);
}

float convertBassFreq (float f){
    return (f/2.0);
}

float playBass(float f, float time, float tStart, float amp, float tLength){
    tLength *= 1.25; // adds lingering effect

    if(time < tStart){
        return (0.0);
    }else{
        float tCurrent = time - tStart;
        if( tCurrent > tLength){
            return (0.0);
        }
        float t = (tCurrent)*convertBassFreq(f);
        //float ampMod = 1.0;//max(0.0, 1.0 - tCurrent/tLength);
        float ratio = tCurrent/tLength;
        ratio *= ratio;
        ratio *= ratio;
        float ampMod = max(0.0, 1.0 - ratio);
        float r = sin(3.14159265359* mod(t,1.0));
        //r = 2.0*ceil(16.0*(r*r))/15. - 1. - 2./15. -(max(0.0, floor( 4.0 * floor( (t-0.375) ) - floor( 4.0 * (t-0.375) ) + 1.0 )));
        //r = 2.0*ceil(16.0*(r*r))/15. - 1. - 2./15.- pow(r,256.0);
        //r = 2.0*ceil(16.0*(r*r))/15. - 1. - 2./15. - (max(0.0, floor( 8.0 * floor( (t-0.4375) ) - floor( 8.0 * (t-0.4375) ) + 1.0 )));
        r = 2.0*ceil(16.0*(r*r))/15. - 1. - 2./15. - (max(0.0, floor( 10.0 * floor( (t-0.45) ) - floor( 10.0 * (t-0.45) ) + 1.0 )));
        //float r2 = sin(mod(t,1.0)*3.14159265359);
        //r2 = 2.0*r2*r2-1.0;
        //r = (r+r2)/2.0;
        return ampMod*amp*r;
    }
    //return amp*sin( t * 2.0 * 3.14 );
}

float playExtraShortBass(float f, float t, float tStart, float amp){
    return playBass(f,t,tStart,amp,0.08-0.04);
}

float playShortBass(float f, float t, float tStart, float amp){
