}

float playLongBass(float f, float t, float tStart, float amp){
    return playBass(f,t,tStart,amp,0.64-0.04);
}

float convertFreq (float f){
    return (2.0*f);
}

vec2 convertFreq (vec2 f){
    return (2.0*f);
}


float play2A03( float f, float t, float tStart, float amp, float tLength){
    tLength *= 1.25; // adds lingering effect

    if (t < tStart){
        return (0.0);
    } else {
        float tCurrent = t - tStart;
        if( tCurrent > tLength){
            return (0.0);
        }
        float ampMod = max(0.0, 1.0 - floor(15.0*tCurrent/tLength)/15.0);
        float newF = convertFreq(f);
        return ampMod*amp*squareWave250(newF, t-tStart);
    }
}

vec2 play2A03( vec2 f, float t, float tStart, float amp, float tLength){
    tLength *= 1.25; // adds lingering effect

    if (t < tStart){
        return vec2(0.0);
    } else {
        float tCurrent = t - tStart;
        float ampMod = max(0.0, 1.0 - floor(15.0*tCurrent/tLength)/15.0);
        vec2 newF = convertFreq(f);
        return ampMod*amp*squareWave250(newF, t-tStart);
    }
}

