#define KEYBOARD iChannel1
#define KEY_RESET 82

vec4 Cell( in ivec2 p ) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 ).rgba;
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 px = ivec2( fragCoord );

    if (iFrame == 0 || key_down(KEY_RESET)) {
        vec2 f = fragCoord + 0.01 * iTime; 

        vec3 e = vec3(0.5);
        float h = h21(f + 113.);
        float s = step(0.99, h);

        vec3 col = pal(10. * h, e, e, e, 1. * vec3(0,1,2)/3.);
        //col *= s;
        vec2 uv = fragCoord / iResolution.xy;
        col = texture(iChannel2, uv).rgb;


        //col *= step(length((uv-0.5) * iResolution.xy/iResolution.y), 0.4);
        //col = vec3(uv.x * step(length(uv-0.5), 0.4));
        fragColor = vec4(col, h21(col.rg));
        //fragColor = vec4(col, s * h21(f + 50.));
        return;
    }

    // center cell
    vec4 e = Cell(px); 

    // neighbour cells
    vec4 t = Cell(px + ivec2(0,-1));
    vec4 b = Cell(px + ivec2(0,1));
    vec4 l = Cell(px + ivec2(-1,0));
    vec4 r = Cell(px + ivec2(1,0));   

    float a = e.a;

    float vt = abs(t.r-e.r)+abs(t.g-e.g)+abs(t.b-e.g);
    float vb = abs(b.r-e.r)+abs(b.g-e.g)+abs(b.b-e.g);
    float vl = abs(l.r-e.r)+abs(l.g-e.g)+abs(l.b-e.g);
    float vr = abs(r.r-e.r)+abs(r.g-e.g)+abs(r.b-e.g);

    vec4 th = vec4(0);
    float mx = max(max(vt,vb),max(vl,vr));
    float mn = min(min(vt,vb),min(vl,vr));
    int c = 0;

    if (vt == mx) {
        th = t; c++;
    }
    if (vb == mx) {
        th = b; c++;
    }
    if (vl == mx) {
        th = l; c++;
    }
    if (vr == mx) {
        th = r; c++;
    }

    if (mx > 0. * sqrt(2.) && c == 1) {
        if (e.a < th.a) {
            e = mix(e,th,0.9);
           // e.a = mix(e.a, th.a, 0.9) + 0.0;
        }
        else 
            e.rgb += 0.001;
    }    
    e.a += 0.0;
    e = fract(e);

   // if (a == e.a)
     //   e.a += 0.;

  //  e.a = fract(e.a);
    //if (a != e.a)
      //  e.a = mix(a, e.a, h21(e.rg + r.b));

    //e.a += 0.005;

   // e.a -= 0.005;
    //e.a = fract(e.a);

    e = clamp(e, 0., 1.);
	fragColor = e;
}

#define pi 3.1415926535

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)-0.1

#define Rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float smin(float a, float b) {
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Flicker reduction.
// This CA tends to produce structures that flicker between two states.
// Code here copies CA output on odd frames and stays unchanged on even.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (iFrame % 2 == 1)
        fragColor.rgba = texelFetch(iChannel0, ivec2(fragCoord), 0).rgba;
    else
        fragColor.rgba = texelFetch(iChannel1, ivec2(fragCoord), 0).rgba;
}
