void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    if(uv.y > 0.51){
        fragColor = texture(iChannel0, vec2(-1,1) * (vec2(0, 1) - uv));
        return;
    }
    if(uv.y > 0.49) {
        fragColor = vec4(1.0);
        return;
    }


    float factor = sin(iTime + uv.x*50.0*sin(uv.x+iTime/80.0));
    // Time varying pixel color
    vec2 offset = vec2(factor, 0.0)*(uv+factor);
    offset.x += cos(iTime + uv.y);



    offset = offset/100.0;


    fragColor = texture(iChannel0, uv + offset);
}
