#define AA 2

float smin( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 comp( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}


float opExtrussion( in vec3 p, in float sdf, in float h )
{
    vec2 w = vec2( sdf, abs(p.z) - h );
  	return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(vec2(c, -s),vec2( s, c));
}
float opSmoothUnion( float d1, float d2, float k )
{
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
	//float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
	//return mix( d2, d1, h ) - k*h*(1.0-h);
}
float opSmoothSubtraction( float d1, float d2, float k )
{
    float h = max(k-abs(-d1-d2),0.0);
    return max(-d1, d2) + h*h*0.25/k;
	//float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
	//return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float sola(vec2 p){
  vec2 p1=p;
   p1.x*=exp2(p1.y*0.4);

   float d1=sdBox(p1,vec2(0.01,0.3))-0.3;

   p.x=abs(p.x);

   p.x-=0.45;
   p.y-=0.4;
   p*=Rot(-0.4);

   float d2=sdBox(p,vec2(0.2,0.3));

   d1=opSmoothSubtraction(d2,d1,0.1);

   return d1;



}
mat3 rotate_x(float a){float sa = sin(a); float ca = cos(a); return mat3(1.,.0,.0,    .0,ca,sa,   .0,-sa,ca);}
mat3 rotate_y(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,.0,sa,    .0,1.,.0,   -sa,.0,ca);}
mat3 rotate_z(float a){float sa = sin(a); float ca = cos(a); return mat3(ca,sa,.0,    -sa,ca,.0,  .0,.0,1.);}


float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sapato(vec3 p){



	float d1= opExtrussion(p,sola(p.xy),0.2)-0.02;


    vec3 p1=p;
    p1.z-=0.16;
    p1.y-=0.1;
    p1.x-=0.2;
    p1*=rotate_y(1.6);
    p1*=rotate_x(-0.3);
    p1*=rotate_z(1.6);

    p1.z+=sin(p1.x-0.8)*sin(-p1.x*-0.5);



    float d2=sdBox(p1,vec3(0.8,0.5,0.2));

    float d2E=sdBox(p1,vec3(0.8,0.5,0.2));
    vec3 p2=p;
    p2.z=abs(p2.z);
    p2.z-=0.4;
    p2.y-=0.25;
    p2*=rotate_x(-0.5);
    float d3=sdBox(p2,vec3(0.4,0.5,0.1));






    d1=opSmoothSubtraction(d2,d1,0.1);


    d1=opSmoothSubtraction(d3,d1,0.1);



return d1*0.5;


}

vec2 map( in vec3 p, float atime )
{

  vec2 m = iMouse.xy/iResolution.xy;

    p.yz *= Rot(-m.y*3.14+1.);
    p.xz *= Rot(-m.x*6.2831);

   p.z=abs(p.z);
   p.z-=0.5;


   vec3 pp1=p;
   float d1=sapato(pp1);

   vec3 pp=p;
   pp.z/=0.9;
   pp.z-=0.005;
   pp.x-=0.01;
   pp.y/=0.95;


   float d2=sapato(pp);

    vec3 p3=p;

    p3.y-=0.3;
    p3.y=abs(p3.y);
    p3.y-=0.06;
    p3.y=abs(p3.y);
    p3.y-=0.03;
    float d4=sdCappedCylinder(p3,0.17,0.025);

    float d5=sdCappedCylinder(p3,0.16,0.3);

    vec3 p4=p;

    p4.y+=0.5;
    p4.x-=0.13;
    float d6=sdCappedCylinder(p4,0.25,0.05);
    float d7=sdCappedCylinder(p4,0.25,0.2);

    d6=max(d6,-d7);


    d4=max(d4,-d5);

    d1=smin(d1,d4,0.02);
    d1=smin(d1,d6*0.5,0.02);

   vec2 res=vec2(d1,1.0);

   res=comp(res,vec2(d2,2.0));

   d1=max(d1,p.x+0.3);




    res=comp(res,vec2((d1*0.5),3.0));

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd, float time )
{
    vec2 res = vec2(-1.0,-1.0);

    float tmin = 0.5;
    float tmax = 20.0;

    float t = tmin;
    for( int i=0; i<512 && t<tmax; i++ )
    {
        vec2 h = map( ro+rd*t, time );
        if( h.x<0.001 )
        { 
            res = vec2(t,h.y); 
            break;
        }
        t += h.x;
    }

    return res;
}

vec3 calcNormal( in vec3 pos, float time )
{
/*
