		map( pos + e.yyx, time ).x - map( pos - e.yyx, time ).x ) );
*/
    vec3 n = vec3(0.0);
    for( int i=min(iFrame,0); i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(pos+0.0005*e,time).x;
    }
    return normalize(n);    
}

float calcOcclusion( in vec3 pos, in vec3 nor, float time )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.11*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = map( opos, time ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 render( in vec3 ro, in vec3 rd, float time )
{ 
    // sky dome
    vec3 col = textureLod(iChannel0,rd,0.0).rgb;

    vec2 res = castRay(ro,rd, time);
    if( res.y>-0.5 )
    {
        float t = res.x;
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos, time );
        vec3 ref = reflect( rd, nor );

		col = vec3(0.2);
        float ks = 1.0;

        if( res.y>3.5 ) // eyeball
        { 
            col = vec3(0.0);
        } 
        else if( res.y>2.5 ) // iris
        { 
            col = vec3(0.4);
        } 
        else if( res.y>1.5 ) // body
        { 
            col = vec3(1.0,0.0,0.0);
        }
		else // terrain
        {
            col = vec3(0.0);
        }

        // lighting
        vec3  sun_lig = normalize( vec3(0.6, 0.35, 0.5) );
        float sun_dif = clamp(dot( nor, sun_lig ), 0.0, 1.0 );
        vec3  sun_hal = normalize( sun_lig-rd );
        float sun_sha = step(castRay( pos+0.001*nor, sun_lig,time ).y,0.0);
		float sun_spe = ks*pow(clamp(dot(nor,sun_hal),0.0,1.0),8.0)*sun_dif*(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
		float sky_dif = sqrt(clamp( 0.5+0.5*nor.y, 0.0, 1.0 ));
        float bou_dif = sqrt(clamp( 0.1-0.9*nor.y, 0.0, 1.0 ))*clamp(1.0-0.1*pos.y,0.0,1.0);

		vec3 lin = vec3(0.0);
        lin += sun_dif*vec3(8.10,6.00,4.20)*sun_sha;
        lin += sky_dif*vec3(0.50,0.70,1.00);
        lin += bou_dif*vec3(0.40,1.00,0.40);
		col = col*lin;
		col += sun_spe*vec3(8.10,6.00,4.20)*sun_sha;


        col = mix( col, vec3(0.5,0.7,0.9), 1.0-exp( -0.0001*t*t*t ) );

    }

    return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}
mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
#define PI 3.14159
mat3 rotation;

#define ZERO (min(iFrame,0))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    	 vec3 tot = vec3(0.0);

    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5; 


    vec2 p = (-iResolution.xy + 2.0*fragCoord+o)/iResolution.y;
    float time = iTime;

    time *= 0.9;

    // camera	
    float an = 10.57*iMouse.x/iResolution.x;
    vec3  ta = vec3( 0.0, 0.0, 0.4);
    vec3  ro = ta + vec3( 1.3, -0.250, 1.3 );

    mat3 ca = setCamera( ro, ta, 0.0 );



     vec2 m2 = iMouse.xy / iResolution.xy;
    m2.y *= -1.0;
    m2 -= vec2(0.5);

    // initial mouse position
    if(iMouse.xy == vec2(0.0))
    {
        m2= vec2(0.0, 8.0);
    }

    rotation = mat3(1.0);
    rotation = rotx(m2.y * 10.0);
    rotation *= rotx(PI * 0.5);
    rotation *= roty(m2.x * 10.0);




    vec3 rd = ca * normalize( vec3(p,1.8) );



    vec3 col = render( ro, rd, time );

    col = pow( col, vec3(0.4545) );

    tot += col;
    }
     tot /= float(AA*AA);
    fragColor = vec4(tot,1.0);
}
