const float pi = 3.14159;
const float pi2 = 2. * pi / 3.;
const float pi3 = 4. * pi / 3.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
//uv = floor(64. * uv) / 64.;

vec2 dir = uv.xy - 0.5;
float d =2. * length(dir);
float theta =  atan( dir.y,dir.x) / 3.;

float t =  0.5 * iTime;
float q = 0.5 * (1. + sin(30. * theta + 1. * t));

d += 0.1 * q;
d *= (1.25 + 0.25 * cos(0.2 * iTime + pi));
    // Time varying pixel color
    float val2 = cos(30. * theta - 2. * t);
    float val = 1. * atan(1. /cos(theta - t + cos(1. * theta - t)),-val2);

    float val2b = cos(11. * (theta + pi2) - 2. * t);
    float valb = 1. * atan(1. / cos(theta + pi2 - t + cos(theta + pi2 - t)), - val2b);

    float val2c = cos(15. * (theta + pi3) - 2. * t);
    float valc = 1. * atan(1. / cos(theta +pi3 + t + cos(theta + pi3 + t)), - val2c);

      val = smoothstep(d,val,val + 0.5); 
      valb = smoothstep(d,valb,valb + 0.5 );
  valc = smoothstep(d,valc,valc + 0.5);

vec3 col = d * d * (2. * q *d + (1. -q)) * vec3(smoothstep(d,val * valb,valc),
smoothstep(d,valb*valc,val),
smoothstep(d,valc*val,valb)
);

col =  q * col + (1.-q) * vec3(0.2627 * col.x + 0.678 * col.y + 0.0593 * col.z);
//col = 0.25 * (col + max(col,1.-col));

//col = 4.*(1.-col);

    // Output to screen
    fragColor = vec4(col,1.0);
}
