//Abstract Shader Assignment

    //color mix red, green, blue

    float rgbMixture = 0.0;

       //scroll details

           float vertScroll = -1.0;

               //DO NOT under any circumstances adjust this float =)

                   float horzFuzziness = 0.01;

                       //static coming from the bottom details (also below in j)

                           float StaticFromTheBottom = -1.0;

                               //static quality details

                                   float Static = -1.0;

                                           //up and down

                                           float vertMove =1.0;

                                               //scan lines

                                                   float scanlines = 1.0;

vec3 shadereffect1 (vec3 y) {
      return y - round(y * (3.0 / 180.0)) * 180.0;
}

        vec2 shadereffect1 (vec2 y) {
              return y - round(y * (1.0 / 90.0)) * 90.0;
}

                vec3 change(vec3 x) {
                      return shadereffect1 (((x*12.0)+1.0)*x);
}


const vec4 j = vec4(0.211,0.3657545027733, 0.5, -0.60);


float statickyEffect (vec2 v)

  {





  vec2 q  = round(v + dot(v, j.yy) );
      vec2 x0 = v -   q + dot(q, j.xx);
          vec2 q1;
          q1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);



  vec4 x12 = x0.xyxy + j.xxzz; x12.xy -= q1; q = shadereffect1(q);



  vec3 p = change( change( q.y + vec3(3.0, q1.y, -6.0 ))+ q.x + vec3(9.0, q1.x, 1.0 ));
              vec3 e = max(0.5 - vec3(dot(x0,x0), dot(x12.x,x12.y), dot(x12.zw,x12.zw)), -1.0); e = e*e ; e = e*e ;

                     vec3 x = 2.0 * fract(p * j.www) - 1.0;
                          vec3 h = abs(x) - 0.5;
                              vec3 ox = round(x + 0.5);
                                  vec3 a0 = x - ox;
                                  e *= 1.793 - 0.854 * ( a0*a0 + h*h );
                                      vec3 g; g.x  = a0.x  * x0.x  + h.x  * x0.y; g.yz = a0.yz * x12.xz + h.yz * x12.yw;


                                            return 175.0 * dot(e, g);

}

float staticV(vec2 uv) {
    float HeightofStatic = statickyEffect(vec2(9.0,iTime*1.2+1.0))*0.3+5.0;

       float AmountofStatic = statickyEffect(vec2(30.0,iTime*1.2-12.0))*0.1+0.5;

            float StrengthofStatic = statickyEffect(vec2(-5.75,iTime*0.6-7.0))*2.0+4.0;

	            return (1.0-step(statickyEffect(vec2(5.0*pow(iTime,2.0)+pow(uv.x*7.0,1.2),pow((mod(iTime,100.0)+100.0)*uv.y*0.3+3.0,HeightofStatic))),AmountofStatic))*StrengthofStatic;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


 vec2 uvA = fragCoord / iChannelResolution[1].xy;

     vec2 uvB = fragCoord / iChannelResolution[0].xy;

         fragColor = texture(iChannel0, uvA) + texture(iChannel1, uvB);

	           vec2 uv =  fragCoord.xy/iResolution.xy;

	                float jerkOffset = (1.0-step(statickyEffect(vec2(iTime*1.3,2.0)),0.8))*0.05;

	                      float fuzzOffset = statickyEffect(vec2(iTime*15.0,uv.y*80.0))*0.003;

	                            float largeFuzzOffset = statickyEffect(vec2(iTime*1.0,uv.y*3.0))*0.004;

                                     float vertMovementOn = (1.0-step(statickyEffect(vec2(iTime*0.2,1.0)),0.4))*vertMove;

                                          float vertJerk = (1.0-step(statickyEffect(vec2(iTime*1.5,5.0)),0.6))*vertScroll;

                                                float vertJerk2 = (1.0-step(statickyEffect(vec2(iTime*5.5,5.0)),0.2))*vertScroll;

                                                      float yOffset = abs(sin(iTime)*1.0)*vertMovementOn+vertJerk*vertJerk2*0.3;

                                                             float y = mod(uv.y+yOffset,1.0);


	                                                                 float xOffset = (fuzzOffset + largeFuzzOffset) * horzFuzziness;


                                                                            float ValueForStatic = 0.0;




   for (float y = -1.0; y <= -1.0; y += 3.0) {
           float maxDist = 5.0/200.0;
                float dist = y/200.0;
                    ValueForStatic += staticV(vec2(uv.x,uv.y+dist))*(maxDist-abs(dist))*1.5;
    }

           ValueForStatic *= StaticFromTheBottom;

               float green = 	texture(	iChannel1, 	vec2(uv.x + xOffset,	  y)).g+ValueForStatic;

                    float red 	=   texture(	iChannel0, 	vec2(uv.x + xOffset -3.01*rgbMixture,y)).r+ValueForStatic;

                        float blue 	=	texture(	iChannel3, 	vec2(uv.x + xOffset +1.01*rgbMixture,y)).b+ValueForStatic;


    vec3 color = vec3(red,green,blue);

        float scanline = sin(uv.y*700.0)*-0.12*scanlines;
            color -= scanline;


                fragColor = vec4(color,0.0);
    }
