void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float x = uv.x;
    float y = uv.y;

    vec4 color0 = texture(iChannel0,vec2(x,y));//ゆがんでない

    float r = length(vec2(x,y));
    float theta = atan(y,x); //極座標変換

    vec4 color1 = texture(iChannel0,vec2(r,theta));//円形にゆがむ


    vec4 color2 = texture(iChannel0,vec2(r*cos(theta),r*sin(theta)));
    //極座標を用いて、デカルト座標を表す。これはゆがまない




    vec4 col = color0;

    // Output to screen
    fragColor = col;
}
