void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float timeScale = 0.1;
    bool useFetch = true;
    if(useFetch)
    {
        vec3 texSize = iChannelResolution[0];
        fragColor = texelFetch(iChannel0, ivec3(int(uv.x * texSize.x), int(uv.y * texSize.y), int(mod(iTime * timeScale, 1.0) * texSize.z)), 0);
    }
    else
    {
        fragColor = texture(iChannel0, vec3(uv, mod(iTime * timeScale, 1.0)));
    }
}
