// variant of https://shadertoy.com/view/NsSSzW

#define S(v)    smoothstep( 30./R.y,0., v )
#define hash(p) fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)
#define sqr(v)  ( (v) * (v) )

float h; // pos along line
float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    h = clamp(dot(p, b) / dot(b, b), 0., 1.);           // proj coord on line
    return length(p - b * h);                           // dist to segment
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, I,I0,H,
         P = ( 2.*u - R ) / R.y,
         U = 20.*u/R.y,
      // F = vec2(P.y,-P.x);                            // field direction of hairs
         F = vec2(2,2);
         F = 1. + cos(3.14/2.*P);
      // F = normalize(mix(F,vec2(2),.5+.5*sin(2.*iTime))) ; 
    float l = 1e5, d;
    O = vec4(0);

    for( int k=0; k<16; k++ ) {                         // check neighbor cells
        I = vec2(k%4-1,k/4-1);
        for (float n=0.; n<17.; n++) {                  // N hair per cell
            H =  hash(floor(U)+I+n/1.71);               // random position in cell
         // H =  1.5*(hash(floor(U)+I+n/1.71)-.5)+.9;   // random position in cell
            P = I + H + .1*cos(5.*iTime+n+vec2(0,11));  // relative to current cell, + motion
         // d = length(fract(U)-P);                     // ( used only to draw debug points )
         // if ( d < l ) l = d, I0=I; // P0 = P;        // ( used only to draw debug points )
            O = max( O,  
                     S( line(fract(U),I+.5,P-F))        // blend hair strends
                  // S( line(fract(U),I+H,P-F))         // variant without strends
                     * sqr(texture(iChannel0, (floor(U)+I+.5*H) *R.y/20./R ) *h*h) ); // hair color
        }
    }
    O = sqrt(O);
                                                        // debug:
//O = vec4( S(l-.1) );                                  //  show points
//O = S(l-.1) *vec4(mod(floor(U)+I0,2.),.5,1) ;         //  color points(tile)
//O = mix( vec4(mod(floor(U),2.),0,1), O, O.a );        //  show tiles

}

// select a frame in the video

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    O = iChannelTime[1] < .9
          ? texture(iChannel1, U ) 
          : texture(iChannel0, U );
}
