vec4 getCol(float coord)
{
    coord /= iResolution.y;    
    vec4[] cols =vec4[] (vec4(85,205,252,255),vec4(247,168,184,255),vec4(255),vec4(247,168,184,255),vec4(85,205,252,255));//Trans rights!
    int ColNum = 5;    
    if(ColNum == 1) //Better safe than sorry
        return cols[0];
    if(ColNum ==2)
        return mix(cols[0],cols[1],coord);

    float cstep = 1.0 /float(ColNum );

    for(int i  = 1; i < ColNum + 1; i++)
    {
        if(coord > cstep * float(i-1 )
        && coord < cstep*float(i))        
            return cols[int(mod(float(i-1),5.))] / 255.;//Incase number of stripes is bigger than the array
    }    
    return vec4(0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(getCol(fragCoord.y).xyz,1.0);
}
