void mainImage( out vec4 c, vec2 p ) {

    // set position
    vec2 v = iResolution.xy;
    // zoom
    p = (p-v*.5)*.4 / v.y;
    // breathing effect
    //p += p * tan(dot(p, p)*4.-iTime) * .04;
    //p -= p * sin(dot(p, p)*4.-iTime) * .4;

    // accumulate color
    c *= 0.;
    for (float i = .5 ; i < 10. ; i++)

        // fractal formula and rotation
        p = abs(2.*fract(p-.5)-1.) * mat2(cos(.0002*(iTime*3.6)*i*sin(iTime*i*0.01) + .78*vec4(1,7,3,1))),

        // coloration
        c += exp(-abs(p.y)*(sin(iTime*.5)+8.)) * (sin(vec4(cos(iTime*1.5)+2.,sin(iTime),sin(iTime*0.5)+2.,0)*i)*0.5+.15);
    // palette
    c.gb *= .85;

}
