float mod(out float modulo, in float v1, in float v2) {
    return v1 - v2 * floor( v1 / v2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 col = vec4(.3, .5 ,.4,1);

    // variables
    float stripLength = 50.0;
    float stripWidth = 20.0;

    float roadSize = 500.0;

    if (fragCoord.x > iResolution.x / 2.0 - stripWidth/ 2.0 && 
    fragCoord.x < iResolution.x / 2.0 + stripWidth/ 2.0 &&
    mod(floor(fragCoord.y / stripLength), 2.0) == 0.0 )
    {
        col = vec4(1,1,1,1);
    }
    if (fragCoord.x < iResolution.x / 2.0 - roadSize / 2.0 || 
    fragCoord.x > iResolution.x / 2.0 + roadSize / 2.0)
    {
        col = vec4(0,0,0,0);
    }

    // Output to screen
    fragColor = col;
}
