#define time 1. * iTime
#define ZPOS -100.

float PI = acos(-1.);

mat2 rot2d(float a){
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

float cyl(vec3 p, vec3 c){
  return length(p.xy - c.xy) - c.z;
}

vec3 thread(vec3 p, float m, float r, float n, float xm, float ym, float i) {
    p.z += (i * 2. * PI) / (m * n);
    p.x += xm * sin(p.z * m);
    p.y += ym * sin(p.z * m * (n - 1.));

    return p;
}

float recbraid(vec3 p, float m, float r) {
    float d = 10.;
    float n1 = 3.;
    float n2 = 5.;

    for (float i = 0.; i < n1; i += 1.) {
        vec3 p1 = thread(p, m * (.5 + 0.3 * sin(time * .29)), r, n1, 35., 35., i);

        for (float j = 0.; j < n2; j += 1.) {
            p1.xy *= rot2d(time * .1);
            vec3 p2 = thread(p1, m * (1. + 0.4 * sin(time * 0.33)), r, n2, 5., 5., j);

            d = min(d, cyl(p2, vec3(0, 0, r)));
        }
    }

    return d;    
}


float map(vec3 p) {
    float d = 1000.;
    float z = p.z;

    p.xy *= rot2d(time * .2);
    vec3 shift = vec3(0, -60, 0);
    shift.xy *= rot2d(time * .2);

    d = min(d, recbraid(p - shift, .025 , .6));

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 col = vec3(0);
  vec3 ro = vec3(0, 0., ZPOS);
  vec3 rd = normalize(vec3(uv.x, uv.y, 1.));
  rd.yz *= rot2d(-.3);

  float d = 0.;

  for (int i = 0; i < 200; i++) {
    vec3 p = ro + d * rd;
    float ds = map(p);

    if (ds < 0.01 || ds > 100.) {
      break;
    }
    d += ds * .5;
  }

  vec3 p = ro + d * rd;
  vec2 e = vec2(0.01, 0);
  vec3 n = normalize(map(p) - 
    vec3(
      map(p - e.xyy),
      map(p - e.yxy),
      map(p - e.yyx)
    )
  );

  vec3 lp = ro;
  vec3 tl = lp - p;
  vec3 tln = normalize(tl);
  float dif = dot(tln, n);

  col = vec3(dif);
  fragColor = vec4(col, 1.);
}
