void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Create a gray-scale image
    vec4 coloredImage = texture(iChannel0, uv);
    float average = (coloredImage.r + coloredImage.g + coloredImage.b) / 3.0;
    vec4 grayScaledImage = vec4(average, average, average, 1.0);

    // Tint an image by any color
    vec4 rightImage = texture(iChannel1, uv);
    rightImage.r *= 1.1;

    // Perform a "split-screen" effect with two different textures showing side-by-side
    if (uv.x < 0.5) {
        fragColor = grayScaledImage;
    } else {
        fragColor = rightImage;
    }
}
