void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color;

    if (uv.x > 0.5)
        color = texture(iChannel0, uv).xyz;
    else
        color = texture(iChannel1, uv).xyz;

    // Output to screen
    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 rgb_a = texture( iChannel0 , uv ).xyz;
    vec3 rgb_b = texture( iChannel1 , uv ).xyz;

    if (rgb_b.x == 1.0)  
         rgb_a = rgb_a + 0.0333;
    else rgb_a = rgb_a - 0.0333;

    if(rgb_a.x < 0.0) rgb_a = vec3(0.0);
    if(rgb_a.x > 1.0) rgb_a = vec3(1.0);


    //rgb_a = vec3(0);
    fragColor = vec4(rgb_a, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 rgb_a = texture( iChannel0 , uv ).xyz;
    vec3 rgb_b = texture( iChannel1 , uv ).xyz;

    if (rgb_a.x == 1.0 && rgb_b.x == 1.0) 
        rgb_b = vec3(0.0);

    if (rgb_a.x == 0.0 && rgb_b.x == 0.0) 
        rgb_b = vec3(1.0);

    fragColor = vec4(rgb_b, 1.0);
}
