renderpass.code:
#define U 12.
precision mediump float;

highp float N21(vec2 co) {
	float n = dot(co, vec2(12.9898,78.233));
	n = sin(mod(n, 3.1417));
	return fract(n * 43758.5453);
}

vec2 domain(vec2 p) {
	p -= iResolution.xy * .5; // center
	p /= U; // slice
	p.y += p.x * .5; // skew
	vec2 q = floor(p); // group
	vec2 r = fract(p);
	if (r.x < r.y) q.y += .5; // split
	return q / 10.;
}

float noise(float n, float d, vec2 co) {
	float t = sin(iTime * d * 2.1 * n + n * 3.141592653589793 * 2.) * 0.5 + 0.5;
	t *= t * .3 + .6;
	t += sin((co.x-co.y) * 30.)/2.;
	t += N21(co)/2.;
	t /= 3.;
	return t;
}

void mainImage(out vec4 fragColor, in vec2 co) {
	vec2 uv = domain(co), ms = domain(iMouse.xy); 

    float arround = distance(uv, ms); // FIXME unskew?
	float n = N21(uv), d = clamp(2. - arround, 0., 2.);

    vec3 c = vec3(noise(n, d, co));

	fragColor = vec4(c, .5);
}
