void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch( iChannel0, ivec2(U), 0 );
}

#define G(O) pow(O, vec4(1./2.2) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R,
         V = 2.*U-1.; V *= V;


    O = texture(iChannel1, U)  // vignetting correction on my Logi720p.
      * ( 1. + vec4(.07,.03,0,0) + vec4(.6,.2,.2,0)*V.x + vec4(.18,.18,.18,0)*V.y );
    if (U.x>.5) O *= 1.7;

 // O *= .5 / textureLod(iChannel1, u/R, 99.); // auto-qualibration
 // O *= 1.85 ; 
 // O.r *= 1.05;

    float y = abs(U.y-.5); 
    int j = int(6.*U.y);
    if ( U.x < ( y < .34 ? .3 : .5 ) ) O = G( vec4(j==1,j==3,j==5,0) 
                                            + vec4(j==0,j==2,j==4,0) *.5 );
    if ( U.x > ( y < .34 ? .7 : .5 ) ) O = G(U.y<.5 ? vec4(mod(u.x+u.y, 2.) < 1.)
                                                   : vec4(.5) 
                                           );
}
