float sinsq(float x){
   return sin(x)*sin(x);
}
float map(vec2 coord, float t) {
    //t *= 50.0213123123 * 3.1416; makes it look better.! :)
    vec2 uv = ( coord - .5*iResolution.xy ) /iResolution.y;
    float height = uv.y + 0.5;
    float smik_x = uv.x - sin(t*2.0) * 0.2;
    float upper = pow(1.8, -pow(smik_x*smik_x*40.0, 0.7))*1.8;
    float smik_off= 0.6;
    float smik = uv.y+smik_off;
    upper *= pow(2.5, -pow(smik*smik*1., 4.0))*1.0;

        //wave
        smik = uv.y+smik_off;
        uv.y -= (0.1 + sinsq(t+uv.x*1.0-smik*1.0)) * 0.1 * upper;
        smik = uv.y-smik_off;
        uv.y -= sin(1.3*t+uv.x*20.0+smik*3.1) * 0.01 * upper;
        smik = uv.y-smik_off;
        uv.y -= sin(6.0*t+uv.x*40.0-smik*20.4213) * 0.005 * upper;
        smik = uv.y-smik_off;
        uv.y -= sin(7.0*t+uv.x*80.0-smik*13.4213) * 0.0035 * upper;


    float modd = pow( 2.0-clamp(height, 0.0, 1.0)*1.1, 1.4);
    return float(
       abs(uv.x) > pow(1.0 - height*0.83, .8)*.8 
       ? 0.
       : smoothstep ((2.5 - height*1.1)/iResolution.y, 0., abs(  mod(uv.y, .04*modd) - .01*modd ) )
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float buff = 0.0;
    vec3 AA = vec3(0.0, 0.0, -1.0)+1.0;
    for (float x = -AA.x; x <= AA.x; ++x)
    for (float y = -AA.y; y <= AA.y; ++y)
    for (float t = -AA.z; t <= AA.z; ++t)
    buff += map(
        vec2(fragCoord) + vec2(x, y)/(AA.xy + 1.0),
        iTime           + iTimeDelta*(t/(AA.z+1.0))*3.0
    );

    vec3 AAp1 = AA*2.0 + 1.0;
    float aaCount = AAp1.x * AAp1.y * AAp1.z;
    fragColor = vec4(buff)/aaCount;
}
