void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = texture(iChannel0, fragCoord/iResolution.xy).rgb;

    color = pow( color, vec3(3.0/2.0, 4.0/5.0, 3.0/2.0) );

    fragColor = vec4(color, 1.0);
}
