uint iters = 12u;

bool labyrinth(uvec2 U){
    bvec2 b = bvec2(U & 1u);
    return b.x || b.y;
}

void mainImage(out vec4 O, vec2 U) {
    uvec2 I = uvec2( U/4.+ iTime*16.);
    O = vec4(1);
    bool b = false;
    float sum = 0.;
    for(uint i = 0u; i++ < iters;I =(I+I.yx/uvec2(11,13)-I.yx/uvec2(13,11))*2u/3u){
        I += i+1u;
        sum += float(b?1:-1);
        b = !b && labyrinth(I);
        O.x = abs(float(b)*O.x*sum);
        O.xyz = abs(float(b)-abs(O.yzx))/2.;
    }
    if (!b) O = 1.-O;
}
