float PI = 3.14,
     R0 = .5,
     R1 = .45,
     dR = .9,
     m = 3.;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = ( 2. * u - Res ) / Res.y;   
    O = vec4(0);

    for(int i = 1; i <= int(m); i++) {
        float a = float(i) * PI/(float(m)/2.) + iTime * iTime;
        float r = distance(U, R0 * vec2(-sin(a), cos(a)));
        float c = min(exp((-r + R1 * dR)/.02), 1.);
        O += vec4(c, 0., c, 1.) * 0.3;
    }
}
