void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;    

    // Create a "percentage-based" split screen effect of two colors
    const float Screen1WidthPercent = 0.5;
    if (uv.x <= Screen1WidthPercent) {
        fragColor = texture(iChannel0, uv);
    } else {
        fragColor = texture(iChannel1, uv);
    }

    // Draw horizontal red line at y = 50 (in pixel units)
    if (fragCoord.y > 49.0 && fragCoord.y < 51.0) {
        fragColor = vec4(1, 0, 0, 1.0);
    }

    // Brighten the whole image by 10%
    const float brightness = 1.1;
    fragColor *= brightness;
}
