vec3 pattern(vec2 p) {
    vec4 s = texture(iChannel0, vec2(0));
    float p1 = mix(sin(sin(p.x * (sin(p.y) - cos(p.x)))), 
                   sin(sin(p.x)) * (sin(p.y) - cos(p.x)), 
                   -0.5 + tan(s).x);
    float p2 = mix(cos(cos(p.y * (cos(p.x) - sin(p.y)))), 
                   cos(cos(p.y)) * (cos(p.x) - sin(p.y)), 
                   -0.5 + sin(s).x);

    if (p1 > p2) {
        return vec3(0.3);
    }
    return vec3(1);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = 24. * uv - 12.;
    p.x *= iResolution.x / iResolution.y;

    vec3 color = pattern(p);
    uv *= 1.0 - uv;
    color *=  vec3(pow(uv.x * uv.y * 15., 0.25)); 

    fragColor = vec4(color, 1.);
}
