void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.y / iResolution.x;
    vec2 uv = fragCoord / iResolution.xy;
    uv.y *= aspectRatio;

    vec3 sunColor = vec3(1.0, 1.0, 0.0);
    float sunRadius = 0.1;
    float sunFrequency = 0.75;
    float sunAmplitude = 0.25;
    vec2 sunPosition = vec2(0.5, 0.0 * aspectRatio);
    vec2 sunCenter = vec2(sunPosition.x, cos(iTime * sunFrequency) * sunAmplitude + sunPosition.y);
    float isTheSun = step(distance(sunCenter, uv), sunRadius);

    uv.y /= aspectRatio;
    float sunsetBrightness = (sunCenter.y + sunRadius) * 3.0;
    vec3 sunset = vec3(1.0, uv.y, 0.0) * sunsetBrightness;

    // Output to screen
    vec3 col = mix(sunset, sunColor, isTheSun);
    fragColor = vec4(col, 1.0);
}
