void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 uv = fragCoord;

    float t = iTime * 1.;
    float k = 6.;
    float doodle = iTime*9.;
    float a = .5 + .5*cos(doodle + uv.x - (uv.x*floor(uv.x/k)) + uv.y - (uv.y*floor(uv.y/k)));
    float b1 = (t-floor(t));
    float b = a; // + b1;
    //(uv.x*floor(uv.x / 7.0)) 
    float h = b - (b * floor(b/t));
    float h2 = exp(h)/2.718;


    //vec3 midcol = vec3(1.,1.,1.);
    //vec3 cola = mix(vec3(1.,.0,1.), vec3(1.,0.,0.), h2);
    //vec3 colb = mix(vec3(0.,1.,1.), vec3(0.,.5,1.), h2);

    //vec3 cola = mix(vec3(2.,.0,.0), vec3(0.,1.,2.), h);
    //vec3 colb = mix(vec3(1.,1.,1.), vec3(0.,.0,2.), h2);
    //vec3 col = mix(cola, colb, h);

    //vec3 cola = mix(vec3(1,1,0), vec3(1,0,1), h);
    //vec3 colb = mix(vec3(1,0,1), vec3(0,1,12), h2*h);
    //vec3 col = mix(cola, colb, h2);

    vec3 cola = mix(vec3(1,1,1), vec3(0,1,0), h2*h2);
    vec3 colb = mix(vec3(1,0,0), vec3(0,1,3), h2);
    vec3 col = mix(cola, colb, (h2+h)*.5);


    //vec3 col = vec3(1.-h2*2., (.5-h2)*(.5-h), h2*(abs(.25-h)+1.));
    // vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    // Output to screen
    fragColor = vec4(col,1.0);
}
