# define PI 3.141592

float scircle(vec2 st, vec2 pos, float r, float s) {
    float m = distance(pos, st);
    return 1.0 - smoothstep(r-s, r+s, m);
}

float circle(vec2 st, vec2 pos, float r) {
    float m = distance(pos, st);
    return 1.0 - step(r, m);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Wave progress range from: 0 -> 1.0
    float p = fract(iTime * 2.5);

    // Wave position
    vec2 pos = iMouse.xy/ iResolution.xy;
    pos.x *= iResolution.x / iResolution.y;

    // Wave radius
    float r = pow(p, .9) / 2.0;
    float rw = 0.01 + 0.05 * pow(p, 0.25);


    // UV
    vec2 st = fragCoord/iResolution.xy;
    st.x *= iResolution.x / iResolution.y;

    // Textures
    vec3 col = texture(iChannel0, st).rgb;
    vec3 col2 = texture(iChannel0, st + vec2(0.1)).rgb;


    // Wave boolean mask
    float m;
    // Create exapnsive circle with a hole in the middle
    m = circle(st, pos, r) * (1.0 - scircle(st, pos, r - rw, 0.025));
    // Fade wave linearly from 0. -> 1.0
    m *= (1.0 - p);

    // Cut texture with wave boolean mask 
    col = (1.0 - m) * col;
    // Paste texture with offset
    col += m * col2;

    // col = vec3(m);


    // Output to screen
    fragColor = vec4(col,1.0);
}
