float Meta(vec3 q1,vec3 q2, float thick, vec2 cpos,float coef){
    float val = 1./length(q1.xy-cpos)*q1.z+1./length(q2.xy-cpos)*q2.z-coef;
    return 1.-abs(val)/thick;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (iResolution.xy-2.*fragCoord)/iResolution.y;
    vec2 mpos = (iMouse.xy*2. - iResolution.xy)/iResolution.y;
    vec3 c1 = vec3(-mpos,.5);
    vec3 c2 = vec3(-0.3,0,.3);
    vec3 col = vec3(1)*Meta(c1,c2,0.1,uv,2.);


    // Output to screen
    fragColor = vec4(col,1.0);
}
