vec3 grid(vec2 a)
{
    vec2 fraction=fract(a);      

    vec3 color=vec3(0.1);
    vec2 grid=floor(mod(a,2.0));
    if(grid.x==grid.y)
    {
        color=vec3(0.6);

    }



    /* color=vec3(smoothstep((fwidth(fraction.x)+0.01),fwidth(fraction.x),fraction.y));
     color+=vec3(smoothstep((fwidth(fraction.x)+0.01),fwidth(fraction.x),fraction.x));
     color.rb*=1.0-smoothstep((fwidth(a.x)+0.02),(fwidth(a.x)+0.01),abs(a.x));
     color.gb*=1.0-smoothstep((fwidth(a.x)+0.02),(fwidth(a.x)+0.01),abs(a.y));*/
    return color;
}



vec2 fixuv(vec2 c)
{
    return (3.0*(2.0*c-iResolution.xy)/iResolution.y);
}




void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fixuv(fragCoord);
    vec3 color=grid(uv);
    fragColor=vec4(color,0.0);    

}
