float distToBox(vec2 pos, float rotation, vec2 size, vec2 uv) {
    float cr = cos(rotation);
    float sr = sin(rotation);

    uv -= pos;
    uv = vec2(uv.x * cr - uv.y * sr, uv.x * sr + uv.y * cr);

    vec2 q = abs(uv) - size;
    return length(max(q,0.0f));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    float b0 = distToBox(iMouse.xy, iTime, vec2(100,50), uv);

    vec3 col = vec3(sin(0.1 * b0));

    fragColor = vec4(col,1.0);
}
